DROP TABLE IF EXISTS `opa_crowd_trace`;
CREATE TABLE `opa_crowd_trace` (
  `trace_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '业务流程踪迹id',
  `trace_oprid` int(11) NOT NULL DEFAULT '0' COMMENT '状态变更操作者id',
  `crowd_id` int(11) NOT NULL DEFAULT '0' COMMENT '所跟踪的拍卖id crowd.id',
  `trace_status_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0: crowd_check 1crowd_onsale',
  `trace_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'crowd_check 或 crowd_onsale的状态值',
  `trace_remarks` varchar(256) NOT NULL DEFAULT '' COMMENT '状态变更备注,有些备注需要在程序中提供默认值',
  `trace_uptime` int(11) NOT NULL DEFAULT '0' COMMENT '状态变更时间',
  PRIMARY KEY (`trace_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='申购业务历史状态变更记录表';

