-- ----------------------------
-- Table structure for `opa_auction`
-- ----------------------------
DROP TABLE IF EXISTS `opa_auction`;
CREATE TABLE `opa_auction` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '拍卖id 旧auction.pid',
  `goods_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品id 新opg_goods.id 旧auction.gid',
  `member_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品所属用户id 旧auction.sellerid 新goods.member_id',
  `business_id` int(11) NOT NULL DEFAULT '0' COMMENT '商户id 旧business.id',
  `auction_oprid` int(11) NOT NULL DEFAULT '0' COMMENT '操作者id 旧auction.aid 新member.id',
  `auction_publishid` int(11) NOT NULL DEFAULT '0' COMMENT '发布人 旧auction.aid',
  `auction_code` varchar(64) NOT NULL DEFAULT '' COMMENT '拍品编号【统一生成订单号】 旧auction.bidnb',
  `auction_name` varchar(256) NOT NULL DEFAULT '' COMMENT '拍卖名称 旧auction.pname',
  `auction_bidcount` int(11) NOT NULL DEFAULT '0' COMMENT '出价次数 旧auction.bidcount',
  `auction_attend_count` int(11) NOT NULL DEFAULT '0' COMMENT '想拍人数 旧auction.clcount',
  `auction_mode` tinyint(3) NOT NULL DEFAULT '10' COMMENT '拍卖模式 旧系统0集市 1专场扣除 2专场单品 3拍卖会 4拍卖会单品 旧auction.pattern 新系统10竞价 11拍卖 12VIP 13专场 14拍卖会',
  `auction_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '拍卖类型0竞拍1竞标 旧auction.type',
  `auction_succtype` tinyint(1) NOT NULL DEFAULT '0' COMMENT '成交模式0普通模式1即时成交 旧auction.succtype',
  `auction_check_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '拍卖审核状态：0待审核 1审核通过 2审核失败',
  `auction_flow_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '拍卖状态流：10正常 11已成交 12流拍 13撤拍 旧auction.endstatus',
  `auction_reason` varchar(256) NOT NULL DEFAULT '' COMMENT '审核失败原因',
  `auction_adjust_status` int(1) NOT NULL DEFAULT '0' COMMENT '拍卖订单调整：0新增，1降价 旧auction.status',
  `auction_sort` int(11) NOT NULL DEFAULT '0' COMMENT '拍卖会排序 旧auction.msort',
  `auction_succ_price` int(11) NOT NULL DEFAULT '0' COMMENT '即时成交价格 旧auction.succprice',
  `auction_freight_price` int(11) NOT NULL DEFAULT '0' COMMENT '运费 旧auction.freight',
  `auction_onset_price` int(11) NOT NULL DEFAULT '0' COMMENT '起拍价 旧auction.onset',
  `auction_reserve_price` int(11) NOT NULL DEFAULT '0' COMMENT '保留价 旧auction.price',
  `auction_now_price` int(11) NOT NULL DEFAULT '0' COMMENT '当前价 旧auctio.nowprice',
  `auction_broker_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '佣金收取方式:新系统0定额 旧auction.broker_type:ratio比例;fixation定额',
  `auction_broker_price` int(11) NOT NULL DEFAULT '0' COMMENT '佣金 旧auction.broker',
  `auction_seller_price` int(11) NOT NULL DEFAULT '0' COMMENT '卖家保证金 旧auction.pledge',
  `auction_buier_price` int(11) NOT NULL DEFAULT '0' COMMENT '买家保证金',
  `auction_stepsize_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '价格浮动方式 0阶梯递增至高位或固定 旧stepsize_type',
  `auction_stepsize_price` int(11) NOT NULL DEFAULT '0' COMMENT '价格浮动金额幅度 旧auction.stepsize',
  `auction_attenderid` int(11) NOT NULL DEFAULT '0' COMMENT '当前出价人id 旧auction.uid',
  `auction_apply_stuff` text COMMENT '卖家申请材料,以||分隔',
  `auction_pledge_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '【预留】旧pledge_type 保证金冻结方式',
  `auction_checkid` int(11) NOT NULL DEFAULT '0' COMMENT '审核人员id',
  `auction_starttime` int(11) NOT NULL DEFAULT '0' COMMENT '拍卖开始时间 旧auction.starttime',
  `auction_endtime` int(11) NOT NULL DEFAULT '0' COMMENT '拍卖结束时间 旧auction.endtime',
  `auction_intime` int(11) NOT NULL DEFAULT '0' COMMENT '插入时间',
  `auction_uptime` int(11) NOT NULL DEFAULT '0' COMMENT '更新时间',
  `auction_onsale` tinyint(1) NOT NULL DEFAULT '0' COMMENT ' 0未上架 1已上架 2已下架',
  `auction_onsale_reason` varchar(255) DEFAULT NULL COMMENT '下架原因',
  PRIMARY KEY (`id`),
  KEY `msort` (`auction_sort`),
  KEY `gid` (`goods_id`),
  KEY `type` (`auction_type`),
  KEY `pattern` (`auction_mode`),
  KEY `status` (`auction_adjust_status`),
  KEY `onset` (`auction_onset_price`),
  KEY `starttime` (`auction_starttime`),
  KEY `endtime` (`auction_endtime`),
  KEY `endstatus` (`auction_flow_status`)
) ENGINE=InnoDB AUTO_INCREMENT=143 DEFAULT CHARSET=utf8 COMMENT='拍卖表 附表auction_scene';

