DROP TABLE IF EXISTS `opa_auction_record`;
CREATE TABLE `opa_auction_record` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `aid` int(11) NOT NULL COMMENT '拍品id',
  `uid` int(11) NOT NULL COMMENT '用户id',
  `username` varchar(20) NOT NULL,
  `time` int(10) NOT NULL COMMENT '出价时间',
  `money` decimal(10,2) NOT NULL COMMENT '出价金额',
  `bided` decimal(10,2) NOT NULL COMMENT '出价后',
  `type` tinyint(2) NOT NULL DEFAULT '0' COMMENT '出价方式0：手动，1：代理',
  PRIMARY KEY (`id`),
  UNIQUE KEY `pid_bided` (`aid`,`bided`) USING BTREE,
  KEY `uid` (`uid`),
  KEY `pid` (`aid`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=225 DEFAULT CHARSET=utf8 COMMENT='拍卖出价记录';

